import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import './AdminPage.css';

const socket = io('http://localhost:4000');

const AdminPage = () => {
  const [orders, setOrders] = useState([]);
  const [carriers, setCarriers] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [selectedCarrier, setSelectedCarrier] = useState(null);
  const [newOrder, setNewOrder] = useState({ products: '', status: '' });

  useEffect(() => {
    const fetchOrders = async () => {
      const response = await fetch('http://localhost:4000/orders');
      const data = await response.json();
      setOrders(data);
    };

    const fetchCarriers = async () => {
      const response = await fetch('http://localhost:4000/carriers');
      const data = await response.json();
      setCarriers(data);
    };

    fetchOrders();
    fetchCarriers();
  }, []);

  const handleAssignCarrier = async () => {
    const response = await fetch('http://localhost:4000/orders/assignCarrier', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        orderId: selectedOrder._id,
        carrierId: selectedCarrier._id,
      }),
    });

    const data = await response.json();
    setSelectedOrder(null);
    setSelectedCarrier(null);
  };

  const handleCreateOrder = async () => {
    const response = await fetch('http://localhost:4000/orders', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newOrder),
    });

    const data = await response.json();
    setOrders([...orders, data]); // Yeni siparişi listeye ekleyin
    setNewOrder({ products: '', status: '' }); // Formu sıfırlayın
  };

  return (
    <div>
      <h1>Admin Page</h1>

      {/* Yeni Sipariş Oluşturma Formu */}
      <div>
        <h2>Create New Order</h2>
        <form onSubmit={(e) => { e.preventDefault(); handleCreateOrder(); }}>
          <div>
            <label>Products (comma-separated):</label>
            <input
              type="text"
              value={newOrder.products}
              onChange={(e) => setNewOrder({ ...newOrder, products: e.target.value })}
              required
            />
          </div>
          <div>
            <label>Status:</label>
            <input
              type="text"
              value={newOrder.status}
              onChange={(e) => setNewOrder({ ...newOrder, status: e.target.value })}
              required
            />
          </div>
          <button type="submit">Create Order</button>
        </form>
      </div>

      {/* Sipariş Listesi */}
      <ul>
        {orders.map(order => (
          <li key={order._id} onClick={() => setSelectedOrder(order)}>
            {order.products.join(', ')} - {order.status}
          </li>
        ))}
      </ul>
      {selectedOrder && (
        <div>
          <h2>Select Carrier for Order: {selectedOrder._id}</h2>
          <select onChange={(e) => setSelectedCarrier(carriers.find(c => c._id === e.target.value))}>
            <option value="">Select Carrier</option>
            {carriers.map(carrier => (
              <option key={carrier._id} value={carrier._id}>
                {carrier.name}
              </option>
            ))}
          </select>
          <button onClick={handleAssignCarrier}>Assign Carrier</button>
        </div>
      )}
    </div>
  );
};

export default AdminPage;
